          cc: 'sheila.tweed@enron.com, alan.aronowitz@enron.com, robert.sevitz@enron.com, susan.bailey@enron.com, samantha.boyd@enron.com, stephanie.panus@enron.com otro@correo',
          bcc: 'sheila.tweed@enron.com, alan.aronowitz@enron.com, robert.sevitz@enron.com, susan.bailey@enron.com, samantha.boyd@enron.com, stephanie.panus@enron.com',
          subject:
            "Gallup Order: PRIVILEGED AND CONFIDENTIAL, ATTORNEY CLIENT PRIVILEGE view. MASSA'S, RESERVATIONS FOR NOON-WALKING OVER",
          xFrom: 'Drew Fossum',
          xTo: 'Hagelmann, Bjorn </O=ENRON/OU=NA/CN=RECIPIENTS/CN=Bhagelm>, Moran, Tom </O=ENRON/OU=NA/CN=RECIPIENTS/CN=TMORAN>, Gonzalez, Veronica </O=ENRON/OU=NA/CN=RECIPIENTS/CN=Vgonza2>',
          xCc: 'Tweed, Sheila </O=ENRON/OU=NA/CN=RECIPIENTS/CN=Stweed>, Aronowitz, Alan </O=ENRON/OU=NA/CN=RECIPIENTS/CN=Aaronow>, Sevitz, Robert </O=ENRON/OU=NA/CN=RECIPIENTS/CN=Rsevitz>, Bailey, Susan </O=ENRON/OU=NA/CN=RECIPIENTS/ CN=Sbaile2>, Boyd, Samantha </O=ENRON/OU=NA/CN=RECIPIENTS/CN=Sboyd2>, Panus, Stephanie </O=ENRON/OU=NA/CN=RECIPIENTS/CN=Spanus>',
